       console.log(currency2);
       console.log(currencyvalue);

        form.addEventListener("submit",function (e) {
            answer.innerText = "Output Answer";
            e.preventDefault();
        });

    fetch(apiURL)
        .then(resp => resp.json())
        .then(data => {

            console.log(data);

            //setRates(data);
            //console.log(data.toString());

        });

    }


    return (
        <div className="App">
