                        <option>BRL</option>
                        <option>RUB</option>
                        <option>HRK</option>
                        <option>JPY</option>
                        <option>THB</option>
                        <option>CHF</option>
                        <option>SGD</option>
                        <option>PLN</option>
                        <option>BGN</option>
                        <option>TRY</option>
                        <option>CNY</option>
                        <option>NOK</option>
                        <option>NZD</option>
                        <option>ZAR</option>
                        <option>USD</option>
                        <option>MXN</option>
                        <option>ILS</option>
                        <option>GBP</option>
                        <option>KRW</option>
                        <option>MYR</option>
                    </select>
                    <input type="number" id="conversionvalue" className="conversionvalue" placeholder="From Value"/>

                    </body>

                    <body>
                    <select id="currencysoptions2">
                        <option>Select Currency</option>
                        <option>EUR</option>
                        <option>CAD</option>
                        <option>HKD</option>
