import React, {useState} from 'react';
import ReactDOM from 'react-dom';
import './styles.css';

function App() {

    const [rates,setRates] = useState(null);
    const apiURL = 'https://api.exchangeratesapi.io/latest';



    function fetchRates(){

       const answer =  document.getElementById("output");
        let converted = rates.toString();
        const form = document.getElementById("mainform");
        form.addEventListener("submit",function (e) {
            answer.innerText = {converted};
            e.preventDefault();
        });

    fetch(apiURL)
        .then(resp => resp.json())
        .then(data => {
            console.log(data);
            setRates(data);

        });

    }


    return (
        <div className="App">
            <h1>Alphonso's Currency Converter</h1>

            {/* Fetch data from API */}
            <div>
                <form id="mainform">
                    <p>Enter Conversion Data</p>
                    <body>
                    <select id="currencysoptions">
                        <option>Select Currency</option>
                        <option>EUR</option>
                        <option>CAD</option>
                        <option>HKD</option>
                        <option>ISK</option>
                        <option>PHP</option>
                        <option>DKK</option>
                        <option>HUF</option>
                        <option>CZK</option>
                        <option>AUD</option>
                        <option>RON</option>
                        <option>SEK</option>
                        <option>IDR</option>
                        <option>BRL</option>
                        <option>RUB</option>
                        <option>HRK</option>
                        <option>JPY</option>
                        <option>THB</option>
                        <option>CHF</option>
                        <option>SGD</option>
                        <option>PLN</option>
                        <option>BGN</option>
                        <option>TRY</option>
                        <option>CNY</option>
                        <option>NOK</option>
                        <option>NZD</option>
                        <option>ZAR</option>
                        <option>USD</option>
                        <option>MXN</option>
                        <option>ILS</option>
                        <option>GBP</option>
                        <option>KRW</option>
                        <option>MYR</option>
                    </select>
                    <input type="number" id="conversionvalue" className="conversionvalue" placeholder="From Value"/>

                    </body>

                    <body>
                    <select id="currencysoptions2">
                        <option>Select Currency</option>
                        <option>EUR</option>
                        <option>CAD</option>
                        <option>HKD</option>
                        <option>ISK</option>
                        <option>PHP</option>
                        <option>DKK</option>
                        <option>HUF</option>
                        <option>CZK</option>
                        <option>AUD</option>
                        <option>RON</option>
                        <option>SEK</option>
                        <option>IDR</option>
                        <option>BRL</option>
                        <option>RUB</option>
                        <option>HRK</option>
                        <option>JPY</option>
                        <option>THB</option>
                        <option>CHF</option>
                        <option>SGD</option>
                        <option>PLN</option>
                        <option>BGN</option>
                        <option>TRY</option>
                        <option>CNY</option>
                        <option>NOK</option>
                        <option>NZD</option>
                        <option>ZAR</option>
                        <option>USD</option>
                        <option>MXN</option>
                        <option>ILS</option>
                        <option>GBP</option>
                        <option>KRW</option>
                        <option>MYR</option>
                    </select>
                    {/*<input type="number" id="conversionvalue2" className="conversionvalue" placeholder="To Value" />*/}

                    </body>


                <button className="fetch-button" onClick={fetchRates}>CONVERT!</button>
                <br />
                </form>
            </div>

            {/* Display data from API */}

            {/* Use JSX below for each book */}
            <div className="rates">

                {/*{rates && rates.map((rate,index) => (*/}

                <h1 id="output"></h1>

                {/*<div className="rate" key={index}>*/}
                {/*    <h3>{rate.base}</h3>*/}


                {/*    <div className="details">*/}
                {/*        <p>{rate.date}</p>*/}
                {/*    </div>*/}
                {/*</div>*/}
                {/*))}*/}

            </div>

        </div>
    );
}

const rootElement = document.getElementById('root');
ReactDOM.render(<App />, rootElement);
